@testable import pokemon_ios
import XCTest

class MainWorkerTests: XCTestCase {
    
    var sut: MainWorker!
    
    override func setUp() {
        super.setUp()
        setupMainWorker()
    }
    
    override func tearDown() {
        super.tearDown()
    }
    
    // setup
    
    func setupMainWorker() {
        sut = MainWorker(mainStore: MainStoreSpy())
    }
    
    // test doubles
    
    class MainStoreSpy: MainStore {
        
        var getPokemonListCalled = false
        
        func getPokemonList(completionHandler: @escaping (APIResourceList?) -> Void) {
            getPokemonListCalled = true
            completionHandler(nil)
        }
        
    }
    
    // tests
    
    func testShouldCallStore() {
        // given
        let mainStoreSpy = MainStoreSpy()
        sut.mainStore = mainStoreSpy
        
        // when
        sut.getPokemonList { _ in }

        // then
        XCTAssert(mainStoreSpy.getPokemonListCalled)
    }
    
}
